sys_script_include.script:
var ScriptValidator = Class.create();
ScriptValidator.prototype = {
    initialize: function() {
		this.ast = null;
		this.context = null;
    },

	runRulesOnAST: function(ast, rules, context){
		if(!ast || !rules) return;

		var astParser = new AstTraverse();
		astParser.parseAndExecuteRules(ast, rules, context);
	},

	executeOnRecord: function(){

	},

	executeOnScript: function(script, fileInfo){
		if(!script) return;
		if (!fileInfo) fileInfo = {};

		this.result = [];
		this.context = {
			file : fileInfo.file || '',
			fileId : fileInfo.fileSysId || '',
			fileType : fileInfo.fileType || '',
			fileMeta : fileInfo.fileMeta || {},
			script : script,
		};

		this.context.report = function(node, msg){
			this.result.push({
				context : this.context,
				node : node,
				message : msg
			})
		};


		var _rHelper = new RulesHelper();
		var _rules = _rHelper.getAllScriptRules();

		var astGenerator = new NowESPrima().esprima;
		var scriptAST = astGenerator.parseScript(script); 		

		this.runRulesOnAST(scriptAST, _rules, this.context);

		return this.result;

	},

	type: 'ScriptValidator'
};
