sys_script_include.script:
var RulesHelper = Class.create();
RulesHelper.prototype = {
    initialize: function() {
		this.__rules = [];
    },

	_loadRules: function() {

	},

	getAllScriptRules: function(){
		var _rules = [];
		var gr = new GlideRecord('x_snc_snlint_rules');
		gr.query();

		while(gr.next()){
			_rules.push({
				name : gr.getValue('name'),
				sys_id: gr.getValue('sys_id')
			});
		}

		return _rules;
	},

	getAllRules: function() {
		return this.__rules;
	},

	getOOBRules: function() {
		return this.__rules;
	},

	getCustomRules: function() {
		return this.__rules;
	},

	/* Get all the matching rule names for a record type*/
	getAllRulesByType: function() {
		return this.__rules;
	},

    type: 'RulesHelper'
};
